//import logo from './logo.svg';
import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './App.css';
import AuthForm from './component/auth/AuthForm';
//import Mailbox from './pages/Mailbox';
import { Route, Switch , Redirect} from 'react-router-dom';
import Routerer from './Routerer/Routerer';
import { inboxmailSliceActions } from "./store/inboxmail";

let initial =true;
function App() {
  const isLoggedIn = useSelector((state) => state.auth.isAuthenticated);
  const dispatch = useDispatch();
  useEffect(() => {
    let setint = setInterval(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          "https://mail-box-43bb3-default-rtdb.firebaseio.com/mail.json"
        );
        const data = await response.json();
        let newArray = [];
        for (let key in data) {
          newArray.push({
            id: key,
            name: data[key].email,
            subject: data[key].subject,
            description: data[key].description,
            time: data[key].time,
            localtime: data[key].localtime,
            read:data[key].read,
            quantity: data[key].q
          });
        }
        dispatch(inboxmailSliceActions.addmail(newArray));
      } catch (err) {
        console.log(err.message);
      }
    }
    // if(initial){
    //   initial=false;
    //   return;
    // }
    fetchData();
    }, 2000);
    return () => setInterval(setint);
  }, [dispatch]);
  return (
    <div >
      <Switch>
      {isLoggedIn && <Routerer />}
      {!isLoggedIn && (
            <Route path="/" exact>
              <Redirect to="/login" />
            </Route>
          )} 
     {!isLoggedIn && <Route path="/login" >
      <AuthForm />
      </Route>}

      {!isLoggedIn && <Route path="*" >
        <Redirect to="/login" />
  </Route>}

      </Switch>
    </div>
  );
}

export default App;
